"use client"

import { useState, useEffect } from "react"
import { Link, useLocation } from "react-router-dom"

const navLinks = [
  { name: "Home", path: "/" },
  { name: "Discover", path: "/projects" },
  { name: "Start a Project", path: "/create-project" },
  { name: "How It Works", path: "/how-it-works" },
]

export default function IndexNavbar() {
  const [navbarOpen, setNavbarOpen] = useState(false)
  const [scrolled, setScrolled] = useState(false)
  const location = useLocation()

  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 10
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled)
      }
    }

    document.addEventListener("scroll", handleScroll)
    return () => {
      document.removeEventListener("scroll", handleScroll)
    }
  }, [scrolled])

  return (
    <nav
      className={`fixed top-0 z-50 w-full transition-all duration-300 ease-in-out ${
        scrolled ? "bg-white shadow-md" : "bg-transparent"
      }`}
    >
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <Link to="/" className="text-xl font-bold text-[#2B3A55] hover:text-[#3B82F6] transition-colors duration-200">
            CrowdFund
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden md:flex md:items-center md:justify-center flex-1 px-4">
            <div className="flex space-x-8">
              {navLinks.map((link) => (
                <Link
                  key={link.name}
                  to={link.path}
                  className={`text-sm font-medium transition-colors duration-200 ${
                    location.pathname === link.path ? "text-[#60A5FA]" : "text-[#64748B] hover:text-[#60A5FA]"
                  }`}
                >
                  {link.name}
                </Link>
              ))}
            </div>
          </div>

          {/* Login Button */}
          <Link
            to="/auth/login"
            className="hidden md:inline-flex bg-[#60A5FA] hover:bg-[#3B82F6] text-white px-6 py-2 rounded-md transition-colors duration-200"
          >
            LOGIN
          </Link>

          {/* Mobile menu button */}
          <button
            onClick={() => setNavbarOpen(!navbarOpen)}
            className="md:hidden inline-flex items-center justify-center p-2 rounded-md text-[#64748B] hover:text-[#60A5FA] hover:bg-gray-100 transition-colors duration-200"
          >
            <span className="sr-only">Open main menu</span>
            <svg
              className={`${navbarOpen ? "hidden" : "block"} h-6 w-6`}
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
            <svg
              className={`${navbarOpen ? "block" : "hidden"} h-6 w-6`}
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        {/* Mobile menu */}
        <div className={`${navbarOpen ? "block" : "hidden"} md:hidden`}>
          <div className="px-2 pt-2 pb-3 space-y-1 bg-white rounded-md shadow-lg">
            {navLinks.map((link) => (
              <Link
                key={link.name}
                to={link.path}
                className={`block px-3 py-2 rounded-md text-base font-medium transition-colors duration-200 ${
                  location.pathname === link.path
                    ? "text-[#60A5FA] bg-gray-50"
                    : "text-[#64748B] hover:text-[#60A5FA] hover:bg-gray-50"
                }`}
              >
                {link.name}
              </Link>
            ))}
            <Link
              to="/auth/login"
              className="block px-3 py-2 rounded-md text-base font-medium text-[#60A5FA] hover:text-white hover:bg-[#60A5FA] transition-colors duration-200"
            >
              LOGIN
            </Link>
          </div>
        </div>
      </div>
    </nav>
  )
}
