"use client"

import { useState, useEffect } from "react"
import { Link, useLocation } from "react-router-dom"

const navLinks = [
  { name: "Home", path: "/" },
  { name: "Discover", path: "/projects" },
  { name: "Start a Project", path: "/create-project" },
  { name: "How It Works", path: "/how-it-works" },
]

export default function IndexNavbar() {
  const [navbarOpen, setNavbarOpen] = useState(false)
  const [scrolled, setScrolled] = useState(false)
  const location = useLocation()

  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 10
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled)
      }
    }

    document.addEventListener("scroll", handleScroll)
    return () => {
      document.removeEventListener("scroll", handleScroll)
    }
  }, [scrolled])

  const toggleNavbar = () => {
    setNavbarOpen(!navbarOpen)
  }

  return (
    <nav
      className={`fixed top-0 z-50 w-full flex flex-wrap items-center justify-between px-2 py-3 transition-all duration-300 ease-in-out ${
        scrolled ? "bg-white shadow" : "bg-transparent"
      }`}
    >
      <div className="container px-4 mx-auto flex flex-wrap items-center justify-between">
        <div className="w-full relative flex justify-between lg:w-auto lg:static lg:block lg:justify-start">
          <Link
            to="/"
            className={`text-sm font-bold leading-relaxed inline-block mr-4 py-2 whitespace-nowrap uppercase ${
              scrolled ? "text-blueGray-700" : "text-white"
            }`}
          >
            CrowdFund
          </Link>
          <button
            className={`cursor-pointer text-xl leading-none px-3 py-1 border border-solid border-transparent rounded bg-transparent block lg:hidden outline-none focus:outline-none ${
              scrolled ? "text-blueGray-700" : "text-white"
            }`}
            type="button"
            onClick={toggleNavbar}
          >
            <i className={`fas ${navbarOpen ? "fa-times" : "fa-bars"}`}></i>
          </button>
        </div>
        <div
          className={`lg:flex flex-grow items-center bg-white lg:bg-opacity-0 lg:shadow-none${
            navbarOpen ? " block rounded shadow-lg" : " hidden"
          }`}
        >
          <ul className="flex flex-col lg:flex-row list-none lg:ml-auto">
            {navLinks.map((link) => (
              <li className="flex items-center" key={link.name}>
                <Link
                  to={link.path}
                  className={`px-3 py-4 lg:py-2 flex items-center text-xs uppercase font-bold ${
                    location.pathname === link.path
                      ? "text-lightBlue-500"
                      : scrolled
                        ? "text-blueGray-700 hover:text-blueGray-500"
                        : "text-white hover:text-blueGray-300"
                  }`}
                >
                  {link.name}
                </Link>
              </li>
            ))}
            <li className="flex items-center">
              <Link
                to="/auth/login"
                className="bg-lightBlue-500 text-white active:bg-lightBlue-600 text-xs font-bold uppercase px-4 py-2 rounded shadow hover:shadow-lg outline-none focus:outline-none lg:mr-1 lg:mb-0 ml-3 mb-3 ease-linear transition-all duration-150"
              >
                Login
              </Link>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  )
}
