"use client"

import { useState } from "react"
import { FaSearch } from "react-icons/fa"
import { Link } from "react-router-dom" // Use React Router for navigation

const categories = [
  "Toutes les catégories",
  "Association",
  "Solidarité",
  "Éducation",
  "Environnement",
  "Santé",
  "Évènement",
  "Anniversaire",
  "Pot de départ",
  "Mariage",
  "Voyage",
  "Cadeau commun",
  "Soirée",
  "Autre",
]

const SearchSection = () => {
  const [searchTerm, setSearchTerm] = useState("")
  const [results, setResults] = useState([])

  const handleSearch = () => {
    console.log(`Searching for: "${searchTerm}"`)
    // Mockup of search results (replace with actual search logic)
    setResults([`Result 1`, `Result 2`])
  }

  return (
    <div className="flex flex-col items-center justify-center">
      {/* Category List as Links */}
      <div className="flex flex-wrap gap-4 mt-6">
        {categories.map((category, index) => (
          <Link
            key={index}
            to={`/category/${category.toLowerCase().replace(/\s+/g, "-")}`} // React Router navigation
            className="px-4 py-2 bg-yellow-500 text-white rounded-md hover:bg-yellow-600"
          >
            {category}
          </Link>
        ))}
      </div>

      {/* Search Bar */}
      <div className="flex mt-6 border rounded-md overflow-hidden">
        <input
          type="text"
          placeholder="Rechercher une cagnotte..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="px-4 py-2 border-r border-gray-300 focus:outline-none w-64"
        />
        <button className="px-4 py-2 bg-blue-600 text-white hover:bg-blue-700" onClick={handleSearch}>
          <FaSearch />
        </button>
      </div>

      {/* Display search results */}
      <div className="mt-4">
        {results.length > 0 ? (
          <ul>
            {results.map((result, index) => (
              <li key={index}>{result}</li>
            ))}
          </ul>
        ) : (
          <p>No results found</p>
        )}
      </div>
    </div>
  )
}

export default SearchSection
