"use client"

import { useState } from "react"
import { FaSearch, FaAngleDown } from "react-icons/fa"

const categories = [
    "Toutes les catégories",
    "Association",
    "Solidarité",
    "Éducation",
    "Environnement",
    "Santé",
    "Évènement",
    "Anniversaire",
    "Pot de départ",
    "Mariage",
    "Voyage",
    "Cadeau commun",
    "Soirée",
    "Autre",
]

const SearchSection = () => {
    const [selectedCategory, setSelectedCategory] = useState("Toutes les catégories")
    const [dropdownOpen, setDropdownOpen] = useState(false)
    const [searchTerm, setSearchTerm] = useState("")
    const [results, setResults] = useState([])

    const handleDropdownClick = () => {
        console.log('Dropdown open:', dropdownOpen);
        setDropdownOpen(!dropdownOpen);
    }
    const handleSearch = () => {
        console.log(`Searching for: "${searchTerm}" in "${selectedCategory}"`)
        // Mockup of search results (replace with actual search logic)
        setResults([`Result 1 in ${selectedCategory}`, `Result 2 in ${selectedCategory}`])
    }

    return (
        <div className="flex flex-col items-center justify-center">
            <div className="flex mt-10 border rounded-md overflow-hidden">
                {/* Dropdown Button */}
                <div className="relative">
                    <button
                        className="px-4 py-2 bg-yellow-500 text-white flex items-center space-x-2"
                        onClick={handleDropdownClick}
                    >
                        <span>{selectedCategory}</span>
                        <FaAngleDown />
                    </button>

                    {/* Dropdown Menu */}
                    {dropdownOpen && (
                        <ul className="absolute left-0 bg-white border w-48 shadow-lg rounded-md mt-1 z-10 max-h-60 overflow-y-auto">
                            {categories.map((category, index) => (
                                <li
                                    key={index}
                                    className="px-4 py-2 hover:bg-gray-200 cursor-pointer"
                                    onClick={() => {
                                        setSelectedCategory(category)
                                        setDropdownOpen(false)
                                    }}
                                >
                                    {category}
                                </li>
                            ))}
                        </ul>
                    )}
                </div>

                {/* Search Input */}
                <input
                    type="text"
                    placeholder="Rechercher une cagnotte..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="px-4 py-2 border-l border-gray-300 focus:outline-none w-64"
                />

                {/* Search Button */}
                <button
  className="px-4 py-2 bg-yellow-500 text-white flex items-center space-x-2"
  onClick={() => {
    console.log("Toggling dropdown:", dropdownOpen);
    setDropdownOpen(!dropdownOpen);
  }}
>
  <span>{selectedCategory}</span>
  <FaAngleDown />
</button>
            </div>

            {/* Display search results */}
            <div className="mt-4">
                {results.length > 0 ? (
                    <ul>
                        {results.map((result, index) => (
                            <li key={index}>{result}</li>
                        ))}
                    </ul>
                ) : (
                    <p>No results found</p>
                )}
            </div>
        </div>
    )
}

export default SearchSection
