"use client"

import { useState } from "react"
import { FaSearch } from "react-icons/fa"

const categories = [
  { label: "Toutes les catégories", value: "all" },
  { label: "Association", value: "association" },
  { label: "Solidarité", value: "solidarity" },
  { label: "Éducation", value: "education" },
  { label: "Environnement", value: "environment" },
  { label: "Santé", value: "health" },
  { label: "Évènement", value: "event" },
  { label: "Anniversaire", value: "birthday" },
  { label: "Pot de départ", value: "farewell" },
  { label: "Mariage", value: "wedding" },
  { label: "Voyage", value: "travel" },
  { label: "Cadeau commun", value: "shared-gift" },
  { label: "Soirée", value: "party" },
  { label: "Autre", value: "other" },
]

const SearchSection = () => {
  const [selectedCategory, setSelectedCategory] = useState("")
  const [searchTerm, setSearchTerm] = useState("")
  const [results, setResults] = useState([])

  const handleCategoryChange = (e) => {
    const category = e.target.value
    setSelectedCategory(category)
  }

  const handleSearch = () => {
    console.log(`Searching for: "${searchTerm}" in "${selectedCategory}"`)
    setResults([`Result 1 in ${selectedCategory}`, `Result 2 in ${selectedCategory}`])
  }

  return (
    <div className="flex flex-col items-center justify-center">
      <div className="flex mt-10 border rounded-md overflow-hidden">
        {/* Category Dropdown */}
        <select
          className="px-4 py-2 bg-yellow-500 text-white border-none w-48 cursor-pointer"
          value={selectedCategory}
          onChange={handleCategoryChange}
        >
          {categories.map((category, index) => (
            <option key={index} value={category.value}>
              {category.label}
            </option>
          ))}
        </select>

        {/* Search Input */}
        <input
          type="text"
          placeholder="Rechercher une cagnotte..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="px-4 py-2 border-l border-gray-300 focus:outline-none w-64"
        />

        {/* Search Button */}
        <button className="px-4 py-2 bg-blue-600 text-white hover:bg-blue-700" onClick={handleSearch}>
          <FaSearch />
        </button>
      </div>

      {/* Display search results */}
      <div className="mt-4">
        {results.length > 0 ? (
          <ul>
            {results.map((result, index) => (
              <li key={index}>{result}</li>
            ))}
          </ul>
        ) : (
          <p>No results found</p>
        )}
      </div>
    </div>
  )
}

export default SearchSection
