"use client"

import { useState, useEffect } from "react"
import { Link, useLocation } from "react-router-dom"

const navLinks = [
  { name: "Home", path: "/" },
  { name: "Discover", path: "/projects" },
  { name: "Start a Project", path: "/create-project" },
  { name: "How It Works", path: "/how-it-works" },
]

export default function IndexNavbar() {
  const [navbarOpen, setNavbarOpen] = useState(false)
  const [scrolled, setScrolled] = useState(false)
  const location = useLocation()

  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 10
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled)
      }
    }

    document.addEventListener("scroll", handleScroll)
    return () => {
      document.removeEventListener("scroll", handleScroll)
    }
  }, [scrolled])

  return (
    <nav
      className={`fixed top-0 z-50 w-full flex flex-wrap items-center justify-between px-2 py-3 transition-all duration-300 ease-in-out ${
        scrolled ? "bg-white shadow" : "bg-transparent"
      }`}
    >
      <div className="container px-4 mx-auto flex flex-wrap items-center justify-between">
        {/* Logo Section */}
        <div className="w-full relative flex justify-between lg:w-auto lg:static lg:block lg:justify-start">
          <Link
            to="/"
            className={`text-xl font-bold leading-relaxed inline-block mr-4 py-2 whitespace-nowrap ${
              scrolled ? "text-[#2B3A55]" : "text-[#2B3A55]"
            }`}
          >
            CrowdFund
          </Link>
          <button
            className={`cursor-pointer text-xl leading-none px-3 py-1 border border-solid border-transparent rounded bg-transparent block lg:hidden outline-none focus:outline-none ${
              scrolled ? "text-[#2B3A55]" : "text-[#2B3A55]"
            }`}
            type="button"
            onClick={() => setNavbarOpen(!navbarOpen)}
          >
            <i className={`fas ${navbarOpen ? "fa-times" : "fa-bars"}`}></i>
          </button>
        </div>

        {/* Navigation Links - Centered */}
        <div
          className={`lg:flex flex-grow items-center justify-center bg-white lg:bg-opacity-0 lg:shadow-none${
            navbarOpen ? " block rounded shadow-lg" : " hidden"
          }`}
        >
          <ul className="flex flex-col lg:flex-row list-none lg:mx-auto">
            {navLinks.map((link) => (
              <li className="flex items-center" key={link.name}>
                <Link
                  to={link.path}
                  className={`px-5 py-4 lg:py-2 flex items-center text-sm font-medium transition-colors duration-200 ${
                    location.pathname === link.path
                      ? "text-[#3B82F6]"
                      : scrolled
                        ? "text-[#2B3A55] hover:text-[#3B82F6]"
                        : "text-[#2B3A55] hover:text-[#3B82F6]"
                  }`}
                >
                  {link.name}
                </Link>
              </li>
            ))}
          </ul>
        </div>

        {/* Login Button - Right Aligned */}
        <div className="hidden lg:flex items-center">
          <Link
            to="/auth/login"
            className="bg-[#3B82F6] text-white text-sm font-medium px-6 py-2 rounded-md hover:bg-[#2563EB] transition-colors duration-200"
          >
            Login
          </Link>
        </div>
      </div>
    </nav>
  )
}
